import React, { Component } from "react";
import { ThemeProvider as MuiThemeProvider } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import { createMuiTheme } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormControl from "@material-ui/core/FormControl";
import FormLabel from "@material-ui/core/FormLabel";

const theme = createMuiTheme({
  palette: {
    primary: {
      main: "#FF9432",
    },
    secondary: {
      main: "#FFF",
    },
  },
});

export class ChooseBowl extends Component {
  continue = (e) => {
    e.preventDefault();
    this.props.nextStep();
  };

  render() {
    const { values, handleChange } = this.props;
    return (
      <MuiThemeProvider theme={theme}>
        <React.Fragment>
          <Container maxWidth="xl" style={{ paddingTop: 100 }}>
            <Grid item xs={12} md={4} lg={4}>
              <Typography variant="h4">
                <b>Make your salad bowl below:</b>
              </Typography>
              <FormControl component="fieldset">
                <FormLabel component="legend" style={{ paddingTop: 16 }}>
                  Choose your bowl
                </FormLabel>
                <RadioGroup
                  name="chooseBowl"
                  defaultValue={values.ChooseBowl}
                  onChange={handleChange}
                  style={{ paddingTop: 16 }}
                >
                  <FormControlLabel
                    value="jumboBowl"
                    control={<Radio />}
                    label="Jumbo bowl - $11.90"
                    style={{ paddingTop: 8, color: "" }}
                  />
                  <FormControlLabel
                    value="mediumBowl"
                    control={<Radio />}
                    label="Medium bowl - $9.90"
                    style={{ paddingTop: 8 }}
                  />
                  <FormControlLabel
                    value="littleBowl"
                    control={<Radio />}
                    label="Little bowl - $7.90"
                    style={{ paddingTop: 8 }}
                  />
                </RadioGroup>
              </FormControl>
              <br /> <br />
              <Button
                label="Continue"
                primary={true}
                color="primary"
                variant="contained"
                onClick={this.continue}
              >
                Continue
              </Button>
            </Grid>
            <Grid item xs={8}></Grid>
          </Container>
        </React.Fragment>
      </MuiThemeProvider>
    );
  }
}

export default ChooseBowl;
