import React from "react";

const Order = () => {
  return (
    <div className="orders">
      <h2 className="bold">Make your salad bowl below:</h2>
      <form>
        <label>Choose size:</label>
        <br></br>
        <select>
          <option value="jumbo">Jumbo bowl - $11.90</option>
          <option value="medium">Medium bowl - $9.90</option>
          <option value="little">Little bowl - $7.90</option>
        </select>
        <button href="confirmation">Continue</button>
      </form>
    </div>
  );
};

export default Order;
