import React, { Component } from "react";
import FormUserDetails from "./FormUserDetails";
import FormPersonalDetails from "./FormPersonalDetails";
import ChooseBowl from "./ChooseBowl";
import ChooseIngredients from "./ChooseIngredients";
import Confirm from "./Confirm";
import Success from "./Success";

export class Order extends Component {
  // state as an object with fields to input
  state = {
    step: 1,
    chooseSize: "",
    chooseGreens: "",
    chooseProtein: "",
    chooseSauce: "",
    firstName: "",
    lastName: "",
    phoneNumber: "",
    email: "",
    blockNumber: "",
    streetName: "",
    unitNumber: "",
    postalCode: "",
  };

  // Proceed to next step
  nextStep = () => {
    const { step } = this.state;
    this.setState({
      step: step + 1,
    });
  };

  // Handle fields change
  handleChange = (input) => (e) => {
    this.setState({ [input]: e.target.value });
  };

  render() {
    const { step } = this.state;
    const {
      chooseSize,
      chooseGreens,
      chooseProtein,
      chooseSauce,
      firstName,
      lastName,
      phoneNumber,
      email,
      blockNumber,
      streetName,
      unitNumber,
      postalCode,
    } = this.state;
    const values = {
      chooseSize,
      chooseGreens,
      chooseProtein,
      chooseSauce,
      firstName,
      lastName,
      phoneNumber,
      email,
      blockNumber,
      streetName,
      unitNumber,
      postalCode,
    };

    switch (step) {
      case 1:
        return (
          <ChooseBowl
            nextStep={this.nextStep}
            handleChange={this.handleChange}
            values={values}
          />
        );
      case 2:
        return (
          <ChooseIngredients
            nextStep={this.nextStep}
            handleChange={this.handleChange}
            values={values}
          />
        );
      case 3:
        return (
          <FormUserDetails
            nextStep={this.nextStep}
            handleChange={this.handleChange}
            values={values}
          />
        );
      case 4:
        return (
          <FormPersonalDetails
            nextStep={this.nextStep}
            prevStep={this.prevStep}
            handleChange={this.handleChange}
            values={values}
          />
        );
      case 5:
        return (
          <Confirm
            nextStep={this.nextStep}
            prevStep={this.prevStep}
            values={values}
          />
        );
      case 6:
        return <Success />;
    }
    return <div></div>;
  }
}

export default Order;
