import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import FormLabel from "@material-ui/core/FormLabel";
import FormControl from "@material-ui/core/FormControl";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormHelperText from "@material-ui/core/FormHelperText";
import Checkbox from "@material-ui/core/Checkbox";
import { ThemeProvider as MuiThemeProvider } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import { createMuiTheme } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Paper from "@material-ui/core/Paper";

const theme = createMuiTheme({
  palette: {
    primary: {
      main: "#FF9432",
    },
    secondary: {
      main: "#FFFFFF",
    },
    white: {
      main: "#1E1E1E",
    },
  },
});



export class ChooseIngredients extends Component {
  continue = (e) => {
    e.preventDefault();
    this.props.nextStep();
  };

  back = (e) => {
    e.preventDefault();
    this.props.prevStep();
  };

  render() {
    const { values, handleChange } = this.props;
    return (
      <MuiThemeProvider theme={theme}>
        <React.Fragment>
          <Container maxWidth="xl" style={{ paddingTop: 100 }}>
            <Typography variant="h4">
              <b>Make your salad bowl below:</b>
            </Typography>
            <Grid container spacing={3}>
              <Grid item xs={12} md={4} lg={4}>
                <Paper
                  className={classes.paper}
                  elevation={0}
                  style={{ paddingTop: 40 }}
                >
                  <FormControl
                    required
                    error={bowlError}
                    component="fieldset"
                    className={classes.formControl}
                  >
                    <FormLabel component="legend">
                      Choose your protein
                    </FormLabel>
                    <FormGroup>
                      <FormControlLabel
                        control={
                          <Checkbox
                            checked={chickenBreast}
                            onChange={handleChange}
                            name="chickenBreast"
                          />
                        }
                        label="Chicken breast"
                        style={{ paddingTop: 8 }}
                      />
                      <FormControlLabel
                        control={
                          <Checkbox
                            checked={chickenThigh}
                            onChange={handleChange}
                            name="chickenThigh"
                          />
                        }
                        label="Chicken Thigh"
                      />
                      <FormControlLabel
                        control={
                          <Checkbox
                            checked={tofu}
                            onChange={handleChange}
                            name="tofu"
                          />
                        }
                        label="Tofu"
                      />
                      <FormControlLabel
                        control={
                          <Checkbox
                            checked={ribeyeSteak}
                            onChange={handleChange}
                            name="ribeyeSteak"
                          />
                        }
                        label="Ribeye steak"
                      />{" "}
                      <FormControlLabel
                        control={
                          <Checkbox
                            checked={salmonFillet}
                            onChange={handleChange}
                            name="salmonFillet"
                          />
                        }
                        label="Salmon Fillet"
                      />
                    </FormGroup>
                    <FormHelperText>Pick maximum 2</FormHelperText>
                  </FormControl>
                </Paper>
              </Grid>
            </Grid>
          </Container>
        </React.Fragment>
      </MuiThemeProvider>
    );
  }
}

export default ChooseIngredients;
