import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import FormLabel from "@material-ui/core/FormLabel";
import FormControl from "@material-ui/core/FormControl";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormHelperText from "@material-ui/core/FormHelperText";
import Checkbox from "@material-ui/core/Checkbox";
import { ThemeProvider as MuiThemeProvider } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import { createMuiTheme } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";

const theme = createMuiTheme({
  palette: {
    primary: {
      main: "#FF9432",
    },
    secondary: {
      main: "#1E1E1E",
    },
  },
});

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(0),
  },
}));

export default function CheckboxesGroup() {
  const classes = useStyles();
  const [state, setState] = React.useState({
    chickenBreast: true,
    chickenThigh: false,
    tofu: false,
    ribeyeSteak: false,
    salmonFillet: false,
  });

  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.checked });
  };

  const { chickenBreast, chickenThigh, tofu, ribeyeSteak, salmonFillet } =
    state;
  const error =
    [chickenBreast, chickenThigh, tofu, ribeyeSteak, salmonFillet].filter(
      (v) => v
    ).length !== 2;

  return (
    <MuiThemeProvider theme={theme}>
      <React.Fragment>
        <Container maxWidth="xl" style={{ paddingTop: 100 }}>
          <Grid item xs={12} md={4} lg={4}>
            <Typography variant="h4">
              <b>Make your salad bowl below:</b>
            </Typography>
            <FormControl
              required
              error={error}
              component="fieldset"
              className={classes.formControl}
            >
              <FormLabel component="legend" style={{ paddingTop: 16 }}>
                Choose your protein
              </FormLabel>
              <FormGroup>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={chickenBreast}
                      onChange={handleChange}
                      name="chickenBreast"
                    />
                  }
                  label="Chicken breast"
                  style={{ paddingTop: 8 }}
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={chickenThigh}
                      onChange={handleChange}
                      name="chickenThigh"
                    />
                  }
                  label="Chicken Thigh"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={tofu}
                      onChange={handleChange}
                      name="tofu"
                    />
                  }
                  label="Tofu"
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={ribeyeSteak}
                      onChange={handleChange}
                      name="ribeyeSteak"
                    />
                  }
                  label="Ribeye steak"
                />{" "}
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={salmonFillet}
                      onChange={handleChange}
                      name="salmonFillet"
                    />
                  }
                  label="Salmon Fillet"
                />
              </FormGroup>
              <FormHelperText>Pick only 2</FormHelperText>
            </FormControl>
          </Grid>

          <Grid item xs={12} md={4} lg={4}>
            <FormControl
              required
              error={error}
              component="fieldset"
              className={classes.formControl}
            >
              <FormLabel component="legend" style={{ paddingTop: 16 }}>
                Choose your greens
              </FormLabel>
              <FormGroup>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={carrot}
                      onChange={handleChange}
                      name="carrot"
                    />
                  }
                  label="Carrot"
                  style={{ paddingTop: 8 }}
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={cherryTomatoes}
                      onChange={handleChange}
                      name="cherryTomatoes"
                    />
                  }
                  label="Cherry tomatoes"
                  style={{ paddingTop: 8 }}
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={cucumber}
                      onChange={handleChange}
                      name="cucumber"
                    />
                  }
                  label="Cucumber"
                  style={{ paddingTop: 8 }}
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={edamame}
                      onChange={handleChange}
                      name="edamame"
                    />
                  }
                  label="Edamame"
                  style={{ paddingTop: 8 }}
                />
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={edamame}
                      onChange={handleChange}
                      name="edamame"
                    />
                  }
                  label="Edamame"
                  style={{ paddingTop: 8 }}
                />
              </FormGroup>
              <FormHelperText>Pick only 2</FormHelperText>
            </FormControl>
          </Grid>
        </Container>
      </React.Fragment>
    </MuiThemeProvider>
  );
}
