import React, { Component } from "react";
import FormUserDetails from "./FormUserDetails";
import FormPersonalDetails from "./FormPersonalDetails";
import Confirm from "./Confirm";

export class Address extends Component {
  state = {
    step: 1,
    firstName: "",
    lastName: "",
    phoneNumber: "",
    email: "",
    blockNumber: "",
    streetName: "",
    unitNumber: "",
    postalCode: "",
  };

  // Proceed to next step
  nextStep = () => {
    const { step } = this.state;
    this.setState({
      step: step + 1,
    });
  };

  // Go back to prev step
  prevStep = () => {
    const { step } = this.state;
    this.setState({
      step: step - 1,
    });
  };

  // Handle fields change
  handleChange = (input) => (e) => {
    this.setState({ [input]: e.target.value });
  };

  render() {
    const { step } = this.state;
    const {
      firstName,
      lastName,
      phoneNumber,
      email,
      blockNumber,
      streetName,
      unitNumber,
      postalCode,
    } = this.state;
    const values = {
      firstName,
      lastName,
      phoneNumber,
      email,
      blockNumber,
      streetName,
      unitNumber,
      postalCode,
    };

    switch (step) {
      case 1:
        return (
          <FormUserDetails
            nextStep={this.nextStep}
            handleChange={this.handleChange}
            values={values}
          />
        );
      case 2:
        return (
          <FormPersonalDetails
            nextStep={this.nextStep}
            prevStep={this.prevStep}
            handleChange={this.handleChange}
            values={values}
          />
        );
      case 3:
        return (
          <FormPersonalDetails
            nextStep={this.nextStep}
            prevStep={this.prevStep}
            handleChange={this.handleChange}
            values={values}
          />
        );
      case 4:
        return <h1>Success</h1>;
    }
    return <div></div>;
  }
}

export default Address;
