import "./App.css";
import { Nav, Navbar } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

function App() {
  const styles = {
    wrapper: {
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      width: "100%",
      padding: "50px",
      color: "#444",
      border: "1px solid #1890ff",
    },
    title: {
      color: "#0d1a26",
      fontWeight: 400,
    },
    button: {
      padding: "10px 20px",
      border: "none",
      borderRadius: "4px",
      background: "#1890ff",
      color: "#fff",
      fontSize: "14px",
      cursor: "pointer",
      transition: ".3s background",
      ":hover": {
        background: "#40a9ff",
      },
    },
  };

  return (
    <div className="App">
      <Navbar className="primary" sticky="top">
        <Navbar.Brand href="#home">
          <h2 className={styles.bold}>the salad bowl</h2>
        </Navbar.Brand>
        <Nav>
          <Nav.Link href="#home">our story</Nav.Link>
          <Nav.Link href="#order">order online</Nav.Link>
          <Nav.Link href="#contact">contact us</Nav.Link>
        </Nav>
      </Navbar>
    </div>
  );
}

export default App;
